#Retrieve all Windows updates from the local computer, and save the output as a variable. $updates = Get-WindowsUpdate
#View all properties of the first returned update. $updates[0]
#View the titles of all returned updates. $updates.Title
#Invoke Get-WindowsUpdate on a Remote Computer $scriptBlock = { function Get-WindowsUpdate { ... }
Get-WindowsUpdate }
Invoke-Command -ComputerName "375-37" -ScriptBlock $scriptBlock

